import './App.css';
import React, { useState, useEffect } from 'react';

function App() {
  var url = `https://s3-ap-southeast-1.amazonaws.com/he-public-data/gamesarena274f2bf.json`;
  async function getData() {
    var result = await Axios.get(url);
    setGames(result.data);
  }
  const [search, setSearch] = useState('');
  const [games, setGames] = useState([]);
  return (
    <div className="App">
      <div className='main'>
        <header className='header' >Games Store</header>
        <form className='main'>
          <div className='main-search'>
            <input type='text' placeholder='Search...' onChange={(e) => setSearch(e.target.value)} />
            {loading ? (<h4>Loading...</h4>) : (games
              .filter((value) => {
                if (search === '') return value;
                else if (values.title.toLowerCase().includes(search.toLowerCase())) {
                  return value;
                }

              })
              .map((items) =>
                <h5 key={items.id}>{item} </h5>
              )




            )}
            <input type='submit' value='Search' />
          </div>


        </form>
        <div className='main-content'></div>

      </div>
    </div>
  );
}

export default App;
